import React from "react"
import { useState, useEffect } from "react";
import './css/Login.css';
import Axios  from "axios";
import Budget from "./budget"
import './css/User_log.css'

export default function Login() {

  
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    // const [id, setid] = useState(0);
    
    const [loginStatus, setLoginStatus] = useState("");

    
    const error = "The username or password is incorrect"
    const login = async() => {
        await Axios.post('http://localhost:4000/login',{
            username: username,
            password: password,

        }).then((response) => {
          if (response.data.message) {
            setLoginStatus(error);
            } else {
            setLoginStatus(response.data[0].username);
            }
        });
    };
    
    const userok = () => {
      return (
        <React.Fragment>
      
        <label htmlFor="email">Username</label>
        <input type="text" id="username" className="log-input" placeholder="  Ej: Lucas23423"
        onChange={(e)=>{
          setUsername(e.target.value);
          console.log("hola")
      }}
        />
        <label htmlFor="password">password</label>
        <input type="text" id="password" className="log-input" placeholder= "  123456"
        onChange={(e)=>{
          setPassword(e.target.value);
      }}
        />
        <button className="button-log"  onClick={login}>Login</button>
        
          
        </React.Fragment>
    );
    } 
    
    return(
      
      <div>
      {userok()}
      
      <h1>{loginStatus}</h1>
      </div>
      // <div>
      
      //   {
      //     loginStatus === '' 
      //       ? userok()
      //       : loginStatus === error
      //       ? userok() 
      //       : <Budget items={username}/>
      //   }
        
      // </div>
    )          
}
