import React from "react"
import { useState, useEffect } from "react";
import './css/Login.css';
import Axios  from "axios";
import Budget from "./budget"
import './css/User_log.css'
import swal from 'sweetalert';

export default function Login() {
  const alerta=()=>{
    swal({
      title: 'Enter username and password'
    })}
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    // const [id, setid] = useState(0);
    
    const [loginStatus, setLoginStatus] = useState("");

    
    const error = "The username or password is incorrect"
    const login = async() => {
        console.log("ddddddddd",username)
        if(!username || !password ){
          alerta()
          console.log("apasaca")
        }else{

        await Axios.post('http://localhost:4000/login',{
            username: username,
            password: password,

        }).then((response) => {
          if (response.data.message) {
            setLoginStatus(error);
            } else {
            setLoginStatus(response.data[0].username);
            }
        });
    }};
    
    const userok = () => {
      return (
        <React.Fragment>
      
        <label htmlFor="email">Username</label>
        <input type="text" className="log-input" placeholder="  Ej: Lucas23423"
        onChange={(e)=>{
          setUsername(e.target.value);
          console.log("hola")
      }}
        />
        <label htmlFor="password">password</label>
        <input type="password" className="log-input" placeholder= "  123456"
        onChange={(e)=>{
          setPassword(e.target.value);
      }}
        />
        <button className="button-log"  onClick={login}>Login</button>
        <h1>{loginStatus}</h1>
          
        </React.Fragment>
    );
    } 
    
    return(
      
    
      <div>
      
        {
          loginStatus === '' 
            ? userok()
            : loginStatus === error
            ? userok() 
            : <Budget items={username}/>
        }
        
      </div>
    )          
}
