import React from "react"
import { useState} from "react";
import Axios  from "axios";
import Budget from "./budget"
import './css/User_log.css'
import swal from 'sweetalert';

export default function Login() {
    console.log("Login")
    const alerta=()=>{
      swal({
        title: 'Enter username and password',
        icon: 'warning',
        button: 'OK',
      })}
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    // const [id, setid] = useState(0);
    const [loginStatus, setLoginStatus] = useState(false);
    
    const login = async(e) => {
        e.preventDefault()
        
        if(!username || !password ){
          alerta()
        }else{
        console.log("Login")
        await Axios.post('http://localhost:4000/login',{
            username: username,
            password: password,

        }).then((response) => {
          if (!response.data.auth) {
            setLoginStatus(false);
          } else {
            localStorage.setItem("token", response.data.token)
            setLoginStatus(true);
          }
        });
    }};
    const userAuthenticated = (e) =>{
      e.preventDefault()
      Axios.get('http://localhost:4000/isUserAuth',{
        headers: {
            "x-access-token": localStorage.getItem("token"),
     }}).then((response)=>{
       console.log(response);
     })
    }
   
    return (
      <React.Fragment>
          
          <label htmlFor="email">Username</label>
          <input type="text" className="log-input" placeholder="  Ej: Lucas23423"
          onChange={(e)=>{
            setUsername(e.target.value);
            console.log(e.target.value)
        }}
          />
          <label htmlFor="password">password</label>
          <input type="password" className="log-input" placeholder= "  123456" 
          onChange={(e)=>{
            setPassword(e.target.value);
        }}
          />
          
          <button className="button-log"  onClick={login}>Login</button>
          {loginStatus === true 
            ?<button onClick={userAuthenticated}>Check if Authenticated</button>
            : <h1></h1>
          }
        
      </React.Fragment>
  );
    
    
       
}
