import React,{ useState, useEffect, useMemo, useHistory} from "react";
import Axios from "axios";

const UsuarioContext = React.createContext();

export function UsuarioProvider(props){
    // let history = useHistory();
    
    const [username, setUsername] = useState('gil');
    const [password, setPassword] = useState('');
    // const [id, setid] = useState(0);
    
    
    const login = async(e) => {
       
    
        await Axios.post('login',{
            username: username,
            password: password,

        }).then((response) => {
          if (!response.data.auth) {
            return false
            
          } else {
            localStorage.setItem("token", response.data.token)
            Axios.get("transactions/40").then(()=>{
            })
            // history.push("/transactions/"+ response.data.result)
           
             
          }
        });
    }
    const value = useMemo(() => {
        
        return ({
          username,
          password,
          setUsername,
          setPassword,
      

        })
      }, [username, password])
         
    
    return <UsuarioContext.Provider value={value} {...props} />

}

export function useUsuario() {
  const context = React.useContext(UsuarioContext);
  if (!context) {
     throw new Error('useUsuario na q ve')
  }
  return context;
}
